import { useEffect } from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector } from 'react-redux';
function App() {
  const isToggle = useSelector(state => state.ui.isVisible);
  const cart = useSelector(state => state.cart.items);
  let respData;
  useEffect(() => {
    fetch('https://fir-tutorial-71007-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json', {
      method: 'PUT',
      body: JSON.stringify(cart),
    })
  }, [cart]);

  useEffect(() => {
    const fetchData = async () => {
      const responseData = await fetch('https://fir-tutorial-71007-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json', {
        method: 'GET'
      })

      respData = responseData.json();
    }

    fetchData();
  }, []);
  console.log(respData);
  return (
    <Layout>
      {isToggle && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
