import {createSlice} from'@reduxjs/toolkit';

export const uiSlice = createSlice({
    name:"ui",
    initialState:{
        isVisible:false,
    },
    reducers:{
        toggleCart(state){
            state.isVisible = !state.isVisible;
        }
    }

})

export default uiSlice;
